const body = $input.item.json.body || $input.item.json;
const imageData = body.image || body.image_data;
const patientId = body.patient_id || `patient_${Date.now()}`;
const imageUrl = body.image_url;

if (!imageData && !imageUrl) {
  throw new Error('No image data or URL provided');
}

return {
  patient_id: patientId,
  image_data: imageData,
  image_url: imageUrl,
  timestamp: new Date().toISOString(),
  request_metadata: {
    source: body.source || 'api',
    priority: body.priority || 'normal'
  }
};

const response = $input.item.json;
const prediction = response.prediction || {};

let severity = 'low';
let riskScore = 0;

if (prediction.is_tumor) {
  riskScore = prediction.confidence * 100;

  if (prediction.confidence > 0.9) {
    severity = 'high';
  } else if (prediction.confidence > 0.75) {
    severity = 'medium';
  } else if (prediction.confidence > 0.6) {
    severity = 'moderate';
  }

  if (prediction.class === 'glioma' && prediction.confidence > 0.85) {
    severity = 'critical';
    riskScore *= 1.2;
  }
}

return {
  patient_id: response.patient_id,
  predicted_class: prediction.class,
  confidence: prediction.confidence,
  confidence_percentage: Math.round(prediction.confidence * 100),
  probabilities: prediction.probabilities,
  is_tumor: prediction.is_tumor,
  severity: severity,
  risk_score: Math.min(100, Math.round(riskScore)),
  gradcam_data: response.gradcam,
  timestamp: response.timestamp,
  metadata: response.metadata
};

const prediction = $input.first().json;
const similarCases = $input.last().json.documents || [];

const context = `You are an expert radiologist AI assistant. Generate a clinical report for this brain MRI analysis.

Patient ID: ${prediction.patient_id}
Diagnosis: ${prediction.predicted_class.toUpperCase()}
Confidence: ${prediction.confidence_percentage}%
Tumor Present: ${prediction.is_tumor ? 'Yes' : 'No'}
Severity: ${prediction.severity.toUpperCase()}
Risk Score: ${prediction.risk_score}/100

Similar Historical Cases:
${similarCases.slice(0, 3).map((c, i) => `${i+1}. ${c}`).join('\n') || 'No similar cases found'}

Generate a professional clinical report including:
1. Diagnosis summary
2. Clinical findings
3. Risk assessment
4. Recommended next steps
5. Urgency level

Keep it concise, professional, and actionable.`;

return {
  ...prediction,
  prompt_context: context,
  similar_cases_count: similarCases.length
};

