const prediction = $input.first().json;
const llmResponse = $input.last().json;

const report = llmResponse.choices[0].message.content;

return {
  patient_id: prediction.patient_id,
  analysis_id: `BTM-${Date.now()}`,
  diagnosis: {
    predicted_class: prediction.predicted_class,
    confidence: prediction.confidence,
    severity: prediction.severity,
    risk_score: prediction.risk_score
  },
  clinical_analysis: {
    llm_generated_report: report,
    similar_cases_analyzed: prediction.similar_cases_count,
    has_historical_precedent: prediction.similar_cases_count > 0
  },
  recommendations: {
    urgent_action_required: prediction.severity === 'critical' || prediction.severity === 'high',
    priority_level: prediction.severity === 'critical' ? 'IMMEDIATE' : 
                   prediction.severity === 'high' ? 'URGENT' : 
                   prediction.severity === 'medium' ? 'ROUTINE' : 'STANDARD'
  },
  metadata: {
    model_version: '1.0',
    rag_enabled: true,
    llm_model: 'groq-llama-3.3-70b',
    llm_provider: 'groq (free)',
    embedding_model: 'sentence-transformers (local, free)',
    vector_db: 'chromadb (local, free)',
    timestamp: new Date().toISOString()
  },
  gradcam_data: prediction.gradcam_data
};

